import java.util.Arrays;
import java.util.Map;
import java.util.HashMap;
import java.util.List;
import java.util.ArrayList;



/**

438. Find All Anagrams in a String

Given a string s and a non-empty string p, find all the start indices of p's anagrams in s.

Strings consists of lowercase English letters only and the length of both strings s and p will not be larger than 20,100.

The order of output does not matter.

Example 1:

Input:
s: "cbaebabacd" p: "abc"

Output:
[0, 6]

Explanation:
The substring with start index = 0 is "cba", which is an anagram of "abc".
The substring with start index = 6 is "bac", which is an anagram of "abc".
Example 2:

Input:
s: "abab" p: "ab"

Output:
[0, 1, 2]

Explanation:
The substring with start index = 0 is "ab", which is an anagram of "ab".
The substring with start index = 1 is "ba", which is an anagram of "ab".
The substring with start index = 2 is "ab", which is an anagram of "ab".

**/




public class FindAllAnagrams{

	// optimal solution with HashMaps

	public List<Integer> findAnagramsII(String s, String p) {
		Map <Character, Integer> sMap = new HashMap<>();
		Map <Character, Integer> pMap = new HashMap<>();

		int np = p.length();
		int ns = s.length();

		// build the ref hashmap usng the string p


		for(char c : p.toCharArray()){
			if(!pMap.containsKey(c)){
				pMap.put(c, 1);
			}else{
				pMap.put(c, pMap.get(c)+1);
			}
		}

		List<Integer> resp = new ArrayList<>();
 
		for(int i = 0; i<s.length(); i++){
			char c = s.charAt(i);

			if(!sMap.containsKey(c)){
				sMap.put(c, 1);
			}else{
				sMap.put(c, sMap.get(c)+1);
			}

			// remove one letter 
      		// from the left side of the window

			if(i >= np){
				c = s.charAt(i - np);
				if(sMap.get(c)==1){
					sMap.remove(c);
				}else{
					sMap.put(c,  sMap.get(c)-1);
				}
			}
			// compare hashmap in the sliding window
      		// with the reference hashmap
			if(pMap.equals(sMap)){
				resp.add(i - np + 1);
			}
		}

		return resp;
	}




	// solution 1  -- exceeds runtime

	public List<Integer> findAnagrams(String s, String p) {
        List<Integer> resp = new ArrayList<>();
        int i = 0;
        int j = p.length();
        while(j<=s.length()){
            String slice = s.substring(i,j);
            if(checkAnagram(slice, p)){
                resp.add(i);
            }
            i++; j++;
        }
        return resp;
    }
    private boolean checkAnagram(String s1, String s2){
        char [] c1 = s1.toCharArray();
        char [] c2 = s2.toCharArray();
        
        Arrays.sort(c1);
        Arrays.sort(c2);
        
        return Arrays.equals(c1,c2);
    }

    public static void main(String[] args) {
    	FindAllAnagrams fa = new FindAllAnagrams();
    	String s = ""; 
    	String p = "";

    	System.out.println("Indexs are "+fa.findAnagramsII(s, p));
    	System.out.println("Indexs are "+fa.findAnagrams(s, p));

    }

}